import React, {useState} from 'react';
import './Note.css';
import TextareaAutosize from '@material-ui/core/TextareaAutosize';
import Button from '@material-ui/core/Button';


function Note() {

    const [note, setNote] = useState('');
    const [read, setRead] = useState(false);

    const addNote = (e) => {
        e.preventDefault();
        if(note?.length === 0){
            alert("Note is empty Please add something");
            // return;
        }
        else{
            setRead(true);
            alert("Note added successfully");
        }
    }

    const editNote = (e) => {
        setRead(false);
    }

    const deleteNote = (e) => {

    }

    return (
        <div className="note">
            <h2>Notes</h2>

            {/* <input  /> */}
            <TextareaAutosize
                className="note__input"
                maxRows={6}
                aria-label="Add notes"
                placeholder="Add notes ..."
                value={note}
                readOnly= {read}
                onChange={e => setNote(e.target.value)}
                disabled
            />

            <div className="note__button">
                <Button variant="contained" onClick={addNote} disabled={!note} color="primary">Add</Button>
                <Button variant="contained" onClick={editNote} disabled={!note} color="primary">Edit</Button>
                <Button variant="contained" onClick={deleteNote} disabled={!note || !read} color="secondary">Delete</Button>
            </div>

            {(read)? (<p></p>) :<p>Add a note </p>}

        </div>
    )
}

export default Note;
