import React, {useState} from 'react';
import './Input.css';

function Input() {

    const [input, setInput] = useState('');

    const addTodo = (e) => {
        e.preventDefault();
        setInput('');
    }

    return (
        <div className="input">
            <input type="text" value={input} onChange={e => setInput(e.target.value)} />
            <button onClick={addTodo} disabled={!input}>
                Add
            </button>
        </div>
    )
}

export default Input;
