import React, {useState, useEffect} from 'react';
import './Note.css';
import TextareaAutosize from '@material-ui/core/TextareaAutosize';
import Button from '@material-ui/core/Button';
import { useDispatch, useSelector } from 'react-redux';
import { selectNote, saveNote } from '../features/notesSlice';

function Note() {

    const [inputNote, setInputNote] = useState('');
    const [add, setAdd] = useState(true);
    const [edit, setEdit] = useState(false);
    const dispatch = useDispatch();
    const note = useSelector(selectNote);

    useEffect(() => {
        if(note?.length > 0){
            setAdd(false);
            setEdit(true);
        }
        else{
            setAdd(true);
            setEdit(false);
        }
    }, [])

    const addNote = (e) => {
        e.preventDefault();
        if(inputNote?.length === 0){
            alert("Note is empty Please add something");
        }
        else{
            dispatch(saveNote(inputNote));
            alert("Note added successfully");
        }
        setInputNote('');
    }

    const editNote = (e) => {
        dispatch(saveNote(inputNote));
        alert("Note edited successfully");
    }

    const deleteNote = (e) => {
        dispatch(saveNote(""));
        alert("Note deleted successfully");
    }

    return (
        <div className="note">
            <h2>Notes</h2>

            <p>{note}</p>

            {/* <input  /> */}
            <TextareaAutosize
                className="note__input"
                maxRows={6}
                aria-label="Add notes"
                placeholder="Add notes ..."
                value={inputNote}
                disabled= {add || edit}
                onChange={e => setInputNote(e.target.value)}
            />

            <div className="note__button">
                <Button variant="contained" onClick={addNote} disabled={add && !edit} color="primary">Add</Button>
                <Button variant="contained" onClick={editNote} disabled={!add && edit} color="primary">Edit</Button>
                <Button variant="contained" onClick={deleteNote} disabled={add && edit} color="secondary">Delete</Button>
            </div>

        </div>
    )
}

export default Note;
