import './Subject.css';
import React, {useState, useEffect} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import InputLabel from '@material-ui/core/InputLabel';
import Input from '@material-ui/core/Input';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import AddBoxIcon from '@material-ui/icons/AddBox';
import DeleteIcon from '@material-ui/icons/Delete';
import EditIcon from '@material-ui/icons/Edit';
import { green } from '@material-ui/core/colors';
import {db} from '../firebase.js';

import { useDispatch, useSelector } from 'react-redux';
import { saveSubject, updateSubjectsList, deleteSubject, selectCurrentSubject,  setCurrentSubject } from '../features/notesSlice';
import { selectSubjectsList } from '../features/notesSlice';

const useStyles = makeStyles((theme) => ({
  container: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
}));

function Subject() {

    const classes = useStyles();
    const [open, setOpen] = useState(false);
    const [subject, setSubject] = useState('');
    const dispatch = useDispatch();
    const subjectsList = useSelector(selectSubjectsList);
    const currentSubject = useSelector(selectCurrentSubject);

    useEffect(() => {
        db.collection("notebooks").get().then((querySnapshot) => {
            querySnapshot.forEach((doc) => {
                // doc.data() is never undefined for query doc snapshots
                console.log(doc.id, " => ", doc.data());
            });
        });
    }, [])

    const handleChange = (event) => {
        setSubject(event.target.value);
        if(event.target.value?.length > 0){
            dispatch(setCurrentSubject(event.target.value));
        }
    };

    const handleClickOpen = () => {
        setOpen(true);
    };

    const handleCloseAdd = () => {
        if(subject?.length > 0){
            dispatch(saveSubject(subject));
        }
        setOpen(false);
    };

    const handleCloseEdit = () => {
        if(subject?.length > 0 && currentSubject !== subject){
            dispatch(updateSubjectsList(
                {old:currentSubject, new:subject}
            ))
        }
        setOpen(false);
    };

    const handleClose = () => {
        setOpen(false);
    };

    const deleteCurrentSubject = () => {
        if(currentSubject?.length > 0){
            dispatch(deleteSubject(currentSubject));
        }
    }

  return (
    <div className="subject">

        <div className="subject__buttons">

            <FormControl variant="filled" className={classes.formControl}>
                <InputLabel id="demo-simple-select-outlined-label">Subject</InputLabel>
                <Select
                    value={currentSubject}
                    onChange={handleChange}
                >
                    {subjectsList.map(item => (
                        (subjectsList?.length > 0) 
                        ? <MenuItem value={item}>{item}</MenuItem>
                        : <MenuItem value=""><em>None</em></MenuItem>
                    ))}

                </Select>
            </FormControl>

            {/* <Button className="subject__addButton" variant="outlined" color="primary" size="large" onClick={handleClickOpen}>Add | Edit</Button> */}
            {/* <Button type="button" className="subject__deleteButton" variant="outlined" color="secondary" size="large" endIcon={<DeleteIcon />} onClick={deleteCurrentSubject}></Button> */}
            <IconButton className="subject__addButton" onClick={handleClickOpen}>
                <AddBoxIcon style={{ color: green[500] }} fontSize="medium" />
            </IconButton>

            <IconButton className="subject__editButton" onClick={handleClickOpen}>
                <EditIcon color="primary" fontSize="medium" />
            </IconButton>

            <IconButton className="subject__deleteButton" onClick={deleteCurrentSubject}>
                <DeleteIcon color="secondary" fontSize="medium" />      
            </IconButton>
        </div>

        <Dialog open={open} onClose={handleClose}>
            <DialogTitle>Add or Edit a Subject</DialogTitle>
            <DialogContent>
                <form className={classes.container}>
                    <FormControl className={classes.formControl}>
                        <InputLabel htmlFor="demo-dialog-native">Subject</InputLabel>
                        <Input value={subject} onChange={e => setSubject(e.target.value)}/>
                    </FormControl>
                </form>
            </DialogContent>

            <DialogActions>
                <Button onClick={handleCloseAdd} color="primary">Add</Button>
                <Button onClick={handleCloseEdit} color="primary">Edit</Button>
            </DialogActions>
        </Dialog>

    </div>
  );
}

export default Subject;
