import './Subject.css';
import React, {useState} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import InputLabel from '@material-ui/core/InputLabel';
import Input from '@material-ui/core/Input';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';


const useStyles = makeStyles((theme) => ({
  container: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
    height: 100,
  },
}));

function Subject() {
  const classes = useStyles();
  const [open, setOpen] = useState(false);
  const [subject, setSubject] = useState('');

  const handleChange = (event) => {
    setSubject(Number(event.target.value) || '');
  };

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <div className="subject">

        <div className="subject__buttons">

            <FormControl className={classes.formControl}>
                <InputLabel htmlFor="demo-dialog-native">{(subject === '') ? "None" : subject}</InputLabel>
                <Select
                    native
                    value={subject}
                    onChange={handleChange}
                    input={<Input id="demo-dialog-native" />}
                >
                    <option aria-label="None" value="" />
                    <option value={'Maths'}>Maths</option>
                    <option value={'Physics'}>Physics</option>
                    <option value={'Chemistry'}>Chemistry</option>
                </Select>
            </FormControl>

            <Button className="subject__addButton" variant="outlined" color="primary" size="large" onClick={handleClickOpen}>Add</Button>
            <Button className="subject__editButton" variant="outlined" color="primary" size="large" onClick={handleClickOpen}>Edit</Button>

        </div>

        <Dialog open={open} onClose={handleClose}>
            <DialogTitle>Add a Subject</DialogTitle>
            <DialogContent>
                <form className={classes.container}>
                    <FormControl className={classes.formControl}>
                        <InputLabel htmlFor="demo-dialog-native">Subject</InputLabel>
                        <Input />
                    </FormControl>
                </form>
            </DialogContent>

            <DialogActions>
                <Button onClick={handleClose} color="primary">
                    Add
                </Button>
            </DialogActions>
        </Dialog>

    </div>
  );
}

export default Subject;
