import { createSlice } from '@reduxjs/toolkit';

const initialState = {
    notes: [],
    subjectsList: ["Maths", "Physics"],
    topics: [],
    currentSubject: '',
    currentTopic: '',
}

const notesSlice = createSlice({
    name: 'notebook',
    initialState,
    reducers: {
        saveSubject: (state, action) => {
            state.subjectsList.push(action.payload);
        },

        saveNote: (state, action) => {
            state.notes.push(action.payload);
        }
    }
});

export const {
    saveSubject, saveNote
} = notesSlice.actions

export const selectSubjectList = state => state.notebook.subjectsList;

export default notesSlice.reducer;
