import './Topic.css';
import React, {useState, useEffect} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import InputLabel from '@material-ui/core/InputLabel';
import Input from '@material-ui/core/Input';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import AddBoxIcon from '@material-ui/icons/AddBox';
import DeleteIcon from '@material-ui/icons/Delete';
import EditIcon from '@material-ui/icons/Edit';
import { green } from '@material-ui/core/colors';
import {db} from '../firebase.js';
import firebase from 'firebase';

import { useDispatch, useSelector } from 'react-redux';
import { saveTopic, updateTopicsList, deleteTopic,  setCurrentTopic, emptyTopicsList } from '../features/notesSlice';
import { selectTopicsList, selectCurrentSubject, selectCurrentTopic } from '../features/notesSlice';

const useStyles = makeStyles((theme) => ({
  container: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
}));

function Topic() {

    const classes = useStyles();
    const [open, setOpen] = useState(false);
    const [topic, setTopic] = useState('');
    const dispatch = useDispatch();
    const topicsList = useSelector(selectTopicsList);
    const currentTopic = useSelector(selectCurrentTopic);
    const currentSubject = useSelector(selectCurrentSubject);

    useEffect(() => {
        if(currentSubject?.length){
            db.collection("notebooks")
                .doc(currentSubject)
                .onSnapshot((doc) => {
                    // console.log(doc.id, " ", doc.data()),
                    doc.data().topicsList?.forEach(item => {
                        dispatch(saveTopic(item))
                    });
                });
        }
        else dispatch(emptyTopicsList([]));
    }, [currentSubject]);

    const handleChange = (event) => {
        setTopic(event.target.value);
        if(event.target.value?.length > 0){
            dispatch(setCurrentTopic(event.target.value));
        }
    };

    const handleClickOpen = () => {
        setOpen(true);
    };

    const handleCloseAdd = () => {
        if(topic?.length && currentSubject?.length){
            db.collection("notebooks")
                .doc(currentSubject)
                .update({
                    topicsList: firebase.firestore.FieldValue.arrayUnion(topic)
                });
        }
        setOpen(false);
    };

    const handleCloseEdit = () => {
        if(topic?.length > 0 && currentTopic !== topic){
            dispatch(updateTopicsList(
                {old:currentTopic, new:topic}
            ))
        }
        setOpen(false);
    };

    const handleClose = () => {
        setOpen(false);
    };

    const deleteCurrentTopic = () => {
        if(currentTopic?.length > 0){
            dispatch(deleteTopic(currentTopic));
        }
    }

  return (
    <div className="topic">

        <div className="topic__buttons">

            <FormControl variant="filled" className={classes.formControl}>
                <InputLabel id="demo-simple-select-outlined-label">Topic</InputLabel>
                <Select
                    value={currentTopic}
                    onChange={handleChange}
                >
                    {topicsList.map(item => (
                        (topicsList?.length > 0) 
                        ? <MenuItem value={item}>{item}</MenuItem>
                        : <MenuItem value=""><em>None</em></MenuItem>
                    ))}

                </Select>
            </FormControl>

            {/* <Button className="subject__addButton" variant="outlined" color="primary" size="large" onClick={handleClickOpen}>Add | Edit</Button> */}
            {/* <Button type="button" className="subject__deleteButton" variant="outlined" color="secondary" size="large" endIcon={<DeleteIcon />} onClick={deleteCurrentTopic}></Button> */}
            <IconButton className="topic__addButton" onClick={handleClickOpen}>
                <AddBoxIcon style={{ color: green[500] }} fontSize="medium" />
            </IconButton>

            <IconButton className="topic__editButton" onClick={handleClickOpen}>
                <EditIcon color="primary" fontSize="medium" />
            </IconButton>

            <IconButton className="topic__deleteButton" onClick={deleteCurrentTopic}>
                <DeleteIcon color="secondary" fontSize="medium" />      
            </IconButton>
        </div>

        <Dialog open={open} onClose={handleClose}>
            <DialogTitle>Add or Edit a Topic</DialogTitle>
            <DialogContent>
                <form className={classes.container}>
                    <FormControl className={classes.formControl}>
                        <InputLabel htmlFor="demo-dialog-native">Topic</InputLabel>
                        <Input value={topic} onChange={e => setTopic(e.target.value)}/>
                    </FormControl>
                </form>
            </DialogContent>

            <DialogActions>
                <Button onClick={handleCloseAdd} color="primary">Add</Button>
                <Button onClick={handleCloseEdit} color="primary">Edit</Button>
            </DialogActions>
        </Dialog>

    </div>
  );
}

export default Topic;
