import './Subject.css';
import React, {useState} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import InputLabel from '@material-ui/core/InputLabel';
import Input from '@material-ui/core/Input';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';

import { useDispatch, useSelector } from 'react-redux';
import { saveSubject,  selectCurrentSubject,  setCurrentSubject } from '../features/notesSlice';
import { selectSubjectsList } from '../features/notesSlice';

const useStyles = makeStyles((theme) => ({
  container: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
}));

function Subject() {

    const classes = useStyles();
    const [open, setOpen] = useState(false);
    const [subject, setSubject] = useState('');
    const dispatch = useDispatch(saveSubject);
    const subjectsList = useSelector(selectSubjectsList);
    const currentSubject = useSelector(selectCurrentSubject);

    const handleChange = (event) => {
        setSubject(event.target.value);
        if(event.target.value?.length > 0){
            dispatch(setCurrentSubject(event.target.value));
        }
    };

    const addSubject = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

  return (
    <div className="subject">

        <div className="subject__buttons">

            <FormControl variant="filled" className={classes.formControl}>
                <InputLabel id="demo-simple-select-outlined-label">Subject</InputLabel>
                <Select
                    value={currentSubject}
                    onChange={handleChange}
                >
                    {subjectsList.map(item => (
                        (subjectsList?.length > 0) 
                        ? <MenuItem value={item}>{item}</MenuItem>
                        : <MenuItem value=""><em>None</em></MenuItem>
                    ))}

                </Select>
            </FormControl>

            <Button className="subject__addButton" variant="outlined" color="primary" size="large" onClick={addSubject}>Add</Button>
            <Button className="subject__editButton" variant="outlined" color="primary" size="large" onClick={editSubject}>Edit</Button>
            <Button className="subject__deleteButton" variant="outlined" color="secondary" size="large" onClick={deleteSubject}>Delete</Button>

        </div>

        <Dialog open={open} onClose={handleClose}>
            <DialogTitle>Add a Subject</DialogTitle>
            <DialogContent>
                <form className={classes.container}>
                    <FormControl className={classes.formControl}>
                        <InputLabel htmlFor="demo-dialog-native">Subject</InputLabel>
                        <Input />
                    </FormControl>
                </form>
            </DialogContent>

            <DialogActions>
                <Button onClick={handleClose} color="primary">
                    Add
                </Button>
            </DialogActions>
        </Dialog>

    </div>
  );
}

export default Subject;
