import { createSlice } from '@reduxjs/toolkit';

const initialState = {
    notes: [],
    subjectsList: ["Maths", "Physics"],
    topicsList: [],
    currentSubject: '',
    currentTopic: '',
}

const notesSlice = createSlice({
    name: 'notebook',
    initialState,
    reducers: {
        saveSubject: (state, action) => {
            state.subjectsList.push(action.payload);
        },

        updateSubjectsList: (state, action) => {
            state.subjectsList= [];
            state.subjectsList.push(action.payload.new);
            state.subjectsList = state.subjectsList.filter(function(v) {
                return v !== action.payload.old;
            });
            state.currentSubject = action.payload.new;
        },

        saveNote: (state, action) => {
            state.notes.push(action.payload);
        },

        setCurrentSubject: (state, action) => {
            state.currentSubject = action.payload;
        }
    }
});

export const {
    saveSubject, saveNote, setCurrentSubject
} = notesSlice.actions

export const selectSubjectsList = state => state.notebook.subjectsList;
export const selectCurrentSubject = state => state.notebook.currentSubject;
export const selectTopicsList = state => state.notebook.topicsList;


export default notesSlice.reducer;
