import { createSlice } from '@reduxjs/toolkit';

const initialState = {
    notes: [],
    subjectsList: ["Maths", "Physics"],
    topicsList: [],
    currentSubject: '',
    currentTopic: '',
}

const notesSlice = createSlice({
    name: 'notebook',
    initialState,
    reducers: {
        saveSubject: (state, action) => {
            let position = state.subjectsList.indexOf(action.payload);
            if(position === -1){
                state.subjectsList.push(action.payload);
                alert("Subject added successfully");
            }
            else{
                alert("Subject already exists");
            }
            state.currentSubject = action.payload;
        },

        updateSubjectsList: (state, action) => {
            let position = state.subjectsList.indexOf(action.payload.old);
            if(position !== -1) 
            state.subjectsList[position] = action.payload.new;
            state.currentSubject = action.payload.new;
            alert("Subject name edited successfully");
        },

        deleteSubject: (state, action) => {
            let position = state.subjectsList.indexOf(action.payload);
            if(position !== -1){
                delete state.subjectsList[position];
                alert("Subject deleted successfully" + state.subjectsList);
                state.currentSubject = state.subjectsList[0];
            }
            else{
                alert("Subject doesn't exists");
                state.currentSubject = "";
            }
        },

        saveNote: (state, action) => {
            state.notes.push(action.payload);
        },

        setCurrentSubject: (state, action) => {
            state.currentSubject = action.payload;
        },

    }
});

export const {
    saveSubject, updateSubjectsList, deleteSubject, saveNote, setCurrentSubject
} = notesSlice.actions

export const selectSubjectsList = state => state.notebook.subjectsList;
export const selectCurrentSubject = state => state.notebook.currentSubject;
export const selectTopicsList = state => state.notebook.topicsList;


export default notesSlice.reducer;
